function is_page(url,array)
{
    for(var i=0; i<array.length;i++)
    {
        object = array[i];
        url = url.toLowerCase();
        url_compare = object.url;
        url_compare = url_compare.toLowerCase();
        if(url==url_compare)
          return object;
    }
    return false;
}
